Rules.InvariantScript:
With(
    {Text: If(varExpandMenu,"Collapse menu","Expand menu")},
    If(
        // If the localized text is blank, return the default text
        IsBlank(
            LookUp(
                colUserLocalizedStrings,
                'English Value' = Text
            ).'Localized Value'
        ),
        Text,
        // If a localized text is found, return that instead of the default text
        LookUp(
            colUserLocalizedStrings,
            'English Value' = Text
        ).'Localized Value'
    )
)

//Toggle global variable of whether or not the menu is expanded.
Set(
    varExpandMenu,
    !varExpandMenu
)

// Define a variable named "Text" with the value "{value}"
With(
    {
        Text: "Divider"
    },
    If(
        // If the localized text is blank, return the default text
        IsBlank(
            LookUp(
                colUserLocalizedStrings,
                'English Value' = Text
            ).'Localized Value'
        ),
        Text,
        // If a localized text is found, return that instead of the default text
        LookUp(
            colUserLocalizedStrings,
            'English Value' = Text
        ).'Localized Value'
    )
)

With(
    {Text: Text(ThisItem.Label)},
    If(
        // If the localized text is blank, return the default text
        IsBlank(
            LookUp(
                colUserLocalizedStrings,
                'English Value' = Text
            ).'Localized Value'
        ),
        Text,
        // If a localized text is found, return that instead of the default text
        LookUp(
            colUserLocalizedStrings,
            'English Value' = Text
        ).'Localized Value'
    )
)

//Onselect and it will navigate to designated screen.
Set(varRescheduleBooking,false);
Navigate(ThisItem.Screen);

With(
    {Text:  ThisItem.Label }, //ThisItem.Label
    If(
        // If the localized text is blank, return the default text
        IsBlank(
            LookUp(
                colUserLocalizedStrings,
                'English Value' = Text
            ).'Localized Value'
        ),
        Text,
        // If a localized text is found, return that instead of the default text
        LookUp(
            colUserLocalizedStrings,
            'English Value' = Text
        ).'Localized Value'
    )
)

// Define a variable named "Text" with the value "{value}"
With(
    {
        Text: "Menu selected"
    },
    If(
        // If the localized text is blank, return the default text
        IsBlank(
            LookUp(
                colUserLocalizedStrings,
                'English Value' = Text
            ).'Localized Value'
        ),
        Text,
        // If a localized text is found, return that instead of the default text
        LookUp(
            colUserLocalizedStrings,
            'English Value' = Text
        ).'Localized Value'
    )
)

// Define a variable named "Text" with the value "{value}"
With(
    {Text: "Main items"},
    // Check if a localized text exists for "Text" in the user's preferred language
    If(
        // If the localized text is blank, return the default text
        IsBlank(
            LookUp(
                colUserLocalizedStrings,
                'English Value' = Text
            ).'Localized Value'
        ),
        Text,
        // If a localized text is found, return that instead of the default text
        LookUp(
            colUserLocalizedStrings,
            'English Value' = Text
        ).'Localized Value'
    )
)

// Define a variable named "Text" with the value "{value}"
With(
    {Text: "Menu groups"},
    // Check if a localized text exists for "Text" in the user's preferred language
    If(
        // If the localized text is blank, return the default text
        IsBlank(
            LookUp(
                colUserLocalizedStrings,
                'English Value' = Text
            ).'Localized Value'
        ),
        Text,
        // If a localized text is found, return that instead of the default text
        LookUp(
            colUserLocalizedStrings,
            'English Value' = Text
        ).'Localized Value'
    )
)

//Expand or collapse the over component width based upon whether or expansion of the menu is selected or not
If(
    varExpandMenu,
    LeftNavigation.SetMenuWith,
    icoLeftNavExpandCollapse.X + icoLeftNavExpandCollapse.Width+1
)
