{
    "type": "AdaptiveCard",
    "$schema": "http://adaptivecards.io/schemas/adaptive-card.json",
    "version": "1.5",
    "body": [
        {
            "type": "TextBlock",
            "text": "Hi @{outputs('Get_Manager_by_Id_in_Users_table')?['body/firstname']},",
            "wrap": true
        },

        {
            "type": "TextBlock",
            "text": "@{outputs('Get_Employee_by_Id_in_Users_table')?['body/fullname']} has submitted a new request.",
            "wrap": true
        },
        {
            "type": "FactSet",
            "facts": [
                {
                    "title": "Type",
                    "value": "@{outputs('Get_Leave_Request')?['body/_mpa_leavetype_label']}"
                },
                {
                    "title": "Comments",
                    "value": "@{coalesce(outputs('Get_Leave_Request')?['body']?['mpa_requestorcomments'], 'none')}"
                },
                {
                    "title": "Start Date",
                    "value": "@{outputs('Get_Leave_Request')?['body/mpa_leavestartdateutc']}"
                },
                {
                    "title": "End Date",
                    "value": "@{outputs('Get_Leave_Request')?['body/mpa_leaveenddateutc']}"
                },
                {
                    "title": "Hour(s) Requested",
                    "value": "@{outputs('Get_Leave_Request')?['body/mpa_hoursrequested']}"
                },
                {
                    "title": "Hour(s) Remaining",
                    "value": "@{outputs('Compose_Hours_Available')}"
                }
            ],
            "spacing": "Medium"
        },
        {
            "type": "TextBlock",
            "text": "If you would like to view this or other requests, simply type approve requests in the chat.",
            "wrap": true,
            "spacing": "Medium"
        },
    ]
}

