Rules.InvariantScript:
// Define a variable named "Text" with the value "{value}"
With(
    {Text: "Details"},
    // Check if a localized text exists for "Text" in the user's preferred language
    If(
        // If the localized text is blank, return the default text
        IsBlank(
            LookUp(
                colUserLocalizedStrings,
                'English Value' = Text
            ).'Localized Value'
        ),
        Text,
        // If a localized text is found, return that instead of the default text
        LookUp(
            colUserLocalizedStrings,
            'English Value' = Text
        ).'Localized Value'
    )
)

//Change FontWeight to semibold if tab currently selected
If(
    LookUp(
        colScreenTabs,
        TabName = "Details"
    ).Screen.Name = App.ActiveScreen.Name,
    FontWeight.Semibold,
    FontWeight.Normal
)

//Navigate to the screen as mapped in the collection
Navigate(
    LookUp(
        colScreenTabs,
        TabName = "Details"
    ).Screen,
    ScreenTransition.None
)

// Define a variable named "Text" with the value "{value}"
With(
    {
        Text: "Tab selected"
    },
    If(
        // If the localized text is blank, return the default text
        IsBlank(
            LookUp(
                colUserLocalizedStrings,
                'English Value' = Text
            ).'Localized Value'
        ),
        Text,
        // If a localized text is found, return that instead of the default text
        LookUp(
            colUserLocalizedStrings,
            'English Value' = Text
        ).'Localized Value'
    )
)

//Show visible only if tab is currently selected
LookUp(
    colScreenTabs,
    TabName = "Details"
).Screen.Name = App.ActiveScreen.Name
